#include <bits/stdc++.h>
using namespace std;
int main()
{
    int a,b,razl=0,pred=0,sb,i=0,b1;
    cin>>a>>b;
    while(a!=0,b!=0)
    {
        sb=a+b;
        if(sb<pred) razl=pred-sb;
        pred=sb;
        i++;
        b1=b;
        cin>>a>>b;
    }
    cout<<razl+b1<<endl;
    return 0;
}
