#include <iostream>
#include <string>
using namespace std;

long long sum, k, kon, ppap;
int d;
short i = 3, p;
string s;

int main () {
    cin >> s;
    d = s.size();
    if (d == 30 || d == 22 || d == 26) ppap --;
    while (sum < d) {
        k ++;
        if (sum + (k*i) <= d) {
            sum += k*i;
            ppap += i;
        }
        else {
            kon = d - sum;
            sum += kon;
            ppap += (kon/k);
        }
        p ++ ;
        if (p == 4) {
            p = 1;
            i = 4;
            ppap ++;
        }
        else i = 3;
        //cout << ppap << endl;
    }
    cout << ppap << endl;
    return 0;
}
