#include<iostream>
using namespace std;
int cnta[26],cntb[26],cnte[26],used1[26],used2[26];
int main() {

	string s;
	cin>>s;
	int sz=s.size();
	for (int i=0 ; i<sz ; i++) {
        for (int j=0 ; j<sz ; j++) {
            if (s[i]-'A'==s[j]-'A') cnte[s[i]-'A']++;
        }
        cnte[s[i]-'A']--;
	}
	for (int i=0 ; i<sz ; i++) {
        for (int j=0 ; j<sz ; j++) {
            if (s[i]-'A'>s[j]-'A') {
                cntb[i]++;
                if (cnte[s[j]-'A']!=0) {
                    if (used1[s[j]-'A']%cnte[s[j]-'A']==0) {
                        cntb[i]-=cnte[s[j]-'A']/cnte[s[j]-'A'];
                        used1[s[j]-'A']++;
                    }
                    else used1[s[j]-'A']++;
                }
            }
            else if (s[i]-'A'<s[j]-'A') {
                cnta[i]++;
                if (cnte[s[j]-'A']!=0) {
                    if (used2[s[j]-'A']%cnte[s[j]-'A']==0) {
                        cnta[i]-=cnte[s[j]-'A']/cnte[s[j]-'A'];
                        used2[s[j]-'A']++;
                    }
                    else {used2[s[j]-'A']++;}
                }
            }
        }
	}
	char c;
	for (int i=0 ; i<sz ; i++) {
        c='A'+cntb[i];
        cout<<c;
	}
	cout<<endl;
	for (int i=0 ; i<sz ; i++) {
        c='Z'-cnta[i];
        cout<<c;
	}
	cout<<endl;
	string g="";
	cout<<g.size();
		return 0;
}

